import Foundation
import Testing
@testable import mew_wallet_ios_kit_bitcoin

@Suite("PSBT.Transaction+Fee tests")
fileprivate struct PSBTTransactionFeeTests {
  @Test("Valid tests", arguments: [
    ("", Result<UInt64, PSBT.Transaction.FeeError>.success(301)),
    ("", .failure(.missingUTXO(index: 0))),
    ("", .success(301)),
    ("", .success(90341)),
    ("", .success(1013)),
    ("cHNidP8BAD8CAAAAAf//////////////////////////////////////////AAAAAAD/////AQAAAAAAAAAAA2oBAAAAAAAACg8BAgMEBQYHCAkPAQIDBAUGBwgJCgsMDQ4PAAA=", .failure(.missingUTXO(index: 0))),
    ("cHNidP8BAF4CAAAAAWbQAKi9hNXynJhqPu8bqkvp0kHihVShkWGh3yLy15+LAAAAAAD/////Aej9AAAAAAAAIlEgRvZJfLLxnVDD6emCqVDcyGIUsB/M5DekIGHHvbEjDTMAAAAAAAEBK7gFAQAAAAAAIlEglCHnNLD50sRn6n3Rl8Yay0RnzcvJ9MsMVx+LY6XEDK4AAA==", .success(2000))
  ])
  func valid(arg: (base64: String, fee: Result<UInt64, PSBT.Transaction.FeeError>)) async throws {
    let decoder = PSBT.Decoder()
    let data = try #require(Data(base64Encoded: arg.base64))
    let tx = try decoder.decode(PSBT.Transaction.self, from: data)
    
    do {
      let expected = try arg.fee.get()
      let result = try tx.fee
      #expect(expected == result)
    } catch {
      #expect(throws: error, performing: {
        try tx.fee
      })
    }
  }
}
